import React, { useState } from 'react';

function ScannedItems({ itemData, setCurrentItem }) {
	const [scannedItems, setScannedItems] = useState([]);

	setScannedItems([...scannedItems, itemData.nameOfItem]);

	console.log('scannedItems: ', scannedItems);

	return (
		<>
			<div className='item-info'>
				<ol>
					{scannedItems.map((item, key) => (
						<li
							className='info-text'
							key={key}
							onClick={setCurrentItem(item)}
						>
							{'  ' + item.nameOfItem}
						</li>
					))}
				</ol>
			</div>
			<button
				className='clear-scans-m'
				onClick={() => setScannedItems([])}
			>
				Clear Scanned Items
			</button>
		</>
	);
}

export default ScannedItems;
