import React from 'react';
import Navbar from './Navbar';

function Inventory() {
	//access camera on device and stream

	var imageCapture;
	var canvas = document.querySelector('canvas');
	var context = canvas.getContext('2d');
	var video = document.querySelector('video');

	function getVideoStream() {
		navigator.mediaDevices
			.getUserMedia({ video: { facingMode: 'environment' } })
			.then((mediaStream) => {
				document.querySelector('video').srcObject = mediaStream;

				const track = mediaStream.getVideoTracks()[0];
				imageCapture = new ImageCapture(track);
			})
			.catch((error) => console.log(error));
	}

	function onGrabFrameButtonClick() {
		imageCapture
			.grabFrame()
			.then((imageBitmap) => {
				const canvas = document.querySelector('canvas');
				drawCanvas(canvas, imageBitmap);
			})
			.catch((error) => console.log(error));
	}

	/*var constraints = { video: { facingMode: 'environment' } };
	const getVideoStream = (constraints) => {
		navigator.mediaDevices
			.getUserMedia(constraints)
			.then(function (stream) {
				var video = document.querySelector('video');
				video.srcObject = stream;
				video.onloadedmetadata = function (e) {
					video.play();
				};
			})
			.catch(function (err) {
				console.log(err);
			});
	};*/
	getVideoStream();

	return (
		<div className='inventory-container'>
			<div className='row'>
				<div id='nav-section' className='col-2'>
					<Navbar />
				</div>
				<div className='col-10'>
					<h1 className='dash-heading'>Take Inventory</h1>
					<hr className='dash-line' />
					<div className='take-inventory'>
						<p className='barcode-p'>Scan barcode:</p>
						<center>
							<video className='video-view'></video>
						</center>
						<center>
							<canvas></canvas>
						</center>
						<center>
							<div>
								use the slider below to represent quantity
								remaining
							</div>
						</center>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Inventory;
