import React from 'react';
import Navbar from './Navbar';

function Inventory() {
	//access camera on device and stream
	var canvas = document.getElementById('canvas');
	var video = document.getElementById('video');
	var photo = document.getElementById('photo');
	var width = 320;
	var height = 0;

	const getVideoStream = () => {
		navigator.mediaDevices
			.getUserMedia({ video: { facingMode: 'environment' } })
			.then((stream) => {
				video.srcObject = stream;
			})
			.catch((error) => console.log(error));
		height = video.videoHeight / (video.videoWidth / width);
		if (isNaN(height)) {
			height = width / (4 / 3);
		}
		video.setAttribute('width', width);
		video.setAttribute('height', height);
		canvas.setAttribute('width', width);
		canvas.setAttribute('height', height);
	};
	const takePic = (e) => {
		e.preventDefault();
		var context = canvas.getContext('2d');
		if (width && height) {
			canvas.width = width;
			canvas.height = height;
			context.drawImage(video, 0, 0, width, height);

			var data = canvas.toDataURL('image/png');
			photo.setAttribute('src', data);
		}
	};

	getVideoStream();

	return (
		<div className='inventory-container'>
			<div className='row'>
				<div id='nav-section' className='col-2'>
					<Navbar />
				</div>
				<div className='col-10'>
					<h1 className='dash-heading'>Take Inventory</h1>
					<hr className='dash-line' />
					<div className='take-inventory'>
						<p className='barcode-p'>Scan barcode:</p>
						<center>
							<video id='video' className='video-view' autoPlay>
								Video stream not available.
							</video>

							<button onClick={(e) => takePic}>Take Photo</button>
							<canvas id='canvas'></canvas>
							<img
								id='photo'
								alt='The screen will capture photo here'
							/>
							<div>
								use the slider below to represent quantity
								remaining
							</div>
						</center>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Inventory;
