import React from 'react';
import Navbar from './Navbar';

function Inventory() {
	//access camera on device and stream
	var canvas = document.getElementById('canvas');
	var video = document.getElementById('video');

	const getVideoStream = () => {
		navigator.mediaDevices
			.getUserMedia({ video: { facingMode: 'environment' } })
			.then((stream) => {
				video.srcObject = stream;
				video.play();
			})
			.catch((error) => console.log(error));
	};
	const takePic = () => {
		var context = canvas.getContext('2d');
		context.drawImage(video, 0, 0, 190, 150);
	};

	getVideoStream();

	const trial = async () => {
		const barcodeDetector = new window.BarcodeDetector({
			formats: [
				'aztec',
				'code_128',
				'code_39',
				'code_93',
				'codabar',
				'data_matrix',
				'ean_13',
				'ean_8',
				'itf',
				'pdf417',
				'qr_code',
				'upc_a',
				'upc_e',
			],
		});
		try {
			const barcodes = await barcodeDetector.detect(canvas);
			console.log(barcodes);
		} catch (e) {
			console.error('Barcode detection failed:', e);
		}
	};

	return (
		<div className='inventory-container'>
			<div className='row'>
				<div id='nav-section' className='col-2'>
					<Navbar />
				</div>

				<div className='col-10'>
					<h1 className='dash-heading'>Take Inventory</h1>
					<hr className='dash-line' />
					<div className='take-inventory'>
						<p className='barcode-p'>Scan barcode:</p>
						<center>
							<video id='video' className='video-view' autoPlay>
								Video stream not available.
							</video>
							<canvas id='canvas'></canvas>
						</center>
						<button onClick={trial}>Take Photo</button>

						<div>
							use the slider below to represent quantity remaining
						</div>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Inventory;
